package com.example.fragment;import com.example.worldtrade.R;import android.app.Activity;import android.content.SharedPreferences;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;public class Fragment5 extends Fragment{	private View parentView;	private String CHINESE;	@Override	@Nullable	public View onCreateView(LayoutInflater inflater,			@Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {		SharedPreferences mySharedPreferences1= getActivity().getSharedPreferences("USER", Activity.MODE_PRIVATE); 		CHINESE =mySharedPreferences1.getString("CHINESE","1");		if(CHINESE.equals("1")){			parentView = inflater.inflate(R.layout.fragment5, container, false);		}else{			parentView = inflater.inflate(R.layout.fragment5e, container, false);		}										return parentView;	}}