package com.example.fragment;import com.example.worldtrade.D1Activity;import com.example.worldtrade.R;import android.app.Activity;import android.content.Intent;import android.content.SharedPreferences;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.view.View.OnClickListener;import android.widget.RelativeLayout;public class Fragment5 extends Fragment{	private View parentView;	private String CHINESE;	private RelativeLayout mRl1;	private RelativeLayout mRl3;	private RelativeLayout mRl2;	private RelativeLayout mRl4;	private RelativeLayout mRl5;	@Override	@Nullable	public View onCreateView(LayoutInflater inflater,			@Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {		SharedPreferences mySharedPreferences1= getActivity().getSharedPreferences("USER", Activity.MODE_PRIVATE); 		CHINESE =mySharedPreferences1.getString("CHINESE","1");		if(CHINESE.equals("1")){			parentView = inflater.inflate(R.layout.fragment5, container, false);		}else{			parentView = inflater.inflate(R.layout.fragment5e, container, false);		}		mRl1 =(RelativeLayout)parentView.findViewById(R.id.mRl1);		mRl2 =(RelativeLayout)parentView.findViewById(R.id.mRl2);		mRl3 =(RelativeLayout)parentView.findViewById(R.id.mRl3);		mRl1.setOnClickListener(listener);		mRl2.setOnClickListener(listener);		mRl3.setOnClickListener(listener);		mRl4 =(RelativeLayout)parentView.findViewById(R.id.mRl4);		mRl5 =(RelativeLayout)parentView.findViewById(R.id.mRl5);		mRl4.setOnClickListener(listener);		mRl5.setOnClickListener(listener);		mRl4.setVisibility(View.GONE);		mRl5.setVisibility(View.GONE);						return parentView;	}	OnClickListener listener =new OnClickListener() {				@Override		public void onClick(View v) {			switch (v.getId()) {			case R.id.mRl1:				Intent intent=new Intent(getActivity(), D1Activity.class);				intent.putExtra("WHAT", "a");				startActivity(intent);				break;			case R.id.mRl2:				Intent intent1=new Intent(getActivity(), D1Activity.class);				intent1.putExtra("WHAT", "b");				startActivity(intent1);				break;			case R.id.mRl3:				Intent intent2=new Intent(getActivity(), D1Activity.class);				intent2.putExtra("WHAT", "c");				startActivity(intent2);				break;			case R.id.mRl4:				Intent intent3=new Intent(getActivity(), D1Activity.class);				intent3.putExtra("WHAT", "d");				startActivity(intent3);				break;			case R.id.mRl5:				Intent intent4=new Intent(getActivity(), D1Activity.class);				intent4.putExtra("WHAT", "e");				startActivity(intent4);				break;			default:				break;			}		}	};}